import React from "react";

const Recipe = ({title,calories,image, ingredientLines}) => {
    return (
        <div>
            <h1>{title}</h1>
            <p>{calories}</p>
            <img src={image} alt={title}/>
            <div>
                {ingredientLines.split("\n").map((i,key) => {
                    return <div key={key}>{i}</div>;
                    })}
            </div>
        </div>
    )
}

export default Recipe;
